mapper.select:
select * from dictionaryplacenames where
name like #{name} and
status=1 and userid=#{userid}

mapper.update:
update  dictionaryplacenames
set name=#{name} ,

pictureurl=#{pictureurl}
where id=#{id}
 and userid=#{userid}

mapper.select:
select *  from dictionaryplacenames where id=(
select max(id)  from dictionaryplacenames
 where status=1  and userid=#{userid}) and status=1  and userid=#{userid}
